html_letter_template = r"""<html><body style="margin:0;font-family:-apple-system,BlinkMacSystemFont,'Segoe UI','Roboto','Oxygen','Ubuntu','Cantarell','Fira Sans','Droid Sans','Helvetica Neue',sans-serif;-webkit-font-smoothing:antialiased;-moz-osx-font-smoothing:grayscale;color:white;"><div style="position: relative; width: 100%; background: #2f679e;"><center><div style="width: 650px; background: #4E7499;"><center><div style="width:600px;left:25px;"><table style="padding-top:25px;"><tbody><tr><td style="height:375px;"><img src="C:\Users\Глеб\Downloads\photo.png" style="width:375px;border-radius:12px;"></td></tr><tr><th style="padding-top:15px;font-size:40px;">Durak Online</th></tr></tbody></table><br><p style="font-size:25px;margin:0;">Здравствуйте, {username}.<br>Ваш код для подтверждения электронной почты:</p><div style="padding:20px;user-select:all;cursor:pointer;" onclick="navigator.clipboard.writeText('{code}')" title="click to copy"><div style="border:2px solid white;padding: 5px 10px;font-size:30px;letter-spacing:5px;display:table;">{code}</div></div><br><hr><p style="padding: 0 0 25px;margin:0;">Данный код действует в течении часа с момента отправки письма</p></div></center></div></center></div></body></html>"""


def render_template(name, code) -> str:
    return html_letter_template.format(username=name, code=code)
